import Modal from "react-modal";
import { Container } from "./style";
import closeModal from "../../assets/close.svg";
import React from "react";
//import { createProject, Project } from "../../api";

interface CreateProjectModalProps {
  isOpen: boolean;
  onRequestClose: () => void;
}

export function CreateProjectModal({
  isOpen,
  onRequestClose,
}: CreateProjectModalProps) {
  const [name, setName] = React.useState();
  //const [newProject, setNewProject] = React.useState<Project>();

  //async function CreateNewProjects() {
  //  const response = await createProject();
  //  setNewProject(response);
  //}

  const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    console.log(e.target);
    const result = {};
    const target = e.target as any;
    target.map((field: { id: string; value: any }) => {
      if (field.id) {
        // @ts-ignore

        result[field.id] = field.value;
      }
    });
    console.log(target, result);
  };

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      overlayClassName="reactModalOverlay"
      className="reactModalContent"
    >
      <button
        type="button"
        onClick={onRequestClose}
        className="reactModalClose"
      >
        <img src={closeModal} alt="close modal" />
      </button>

      <Container onSubmit={onSubmit}>
        <h2 className="span">NEW PROJECT</h2>

        <div className="span">
          <label htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Project Name"
            required
          />
        </div>

        <div className="span">
          <label htmlFor="description">Descriotion</label>
          <textarea id="description" placeholder="Project Description" />
        </div>

        <div>
          <label htmlFor="startDate"> Start Date</label>
          <input type="date" id="startDate" required />
        </div>

        <div>
          <label htmlFor="endDate">End Date</label>
          <input type="date" id="endDate" required />
        </div>

        <button type="submit" className="green">
          Create
        </button>
        <button>Cancel</button>
      </Container>
    </Modal>
  );
}
